  {name: 'Suzuki', country: 'Japan', owner: 'Suzuki Motor Corporation'},
  {name: 'Toyota', country: 'Japan', owner: 'Toyota Motor Corporation'},
  {name: 'Volvo', country: 'Sweden', owner: 'Zhejiang Geely Holding Group'}
];
